tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Màu màn hình</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">TFT 16 triệu màu</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Độ phân giải</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">640 x 960 pixels</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Kích thước màn hình</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">3.5 inches</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Hỗ trợ cảm ứng</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">Cảm ứng điện dung đa điểm</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Hệ điều hành</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">iOS</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Chipset</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">ARM Cortex A9 1GHz dual.core processor</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">CPU</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">Dual-core 1 GHz</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">GPU</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">PowerVR SGX543MP2</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Bộ nhớ trong (<a target="_blank" title="Tìm hiểu thêm về: RO" class="glossary_link vtip" href="http://hc.com.vn/glossary/view/content?id=7">RO</a>M)</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">16 GB</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">RAM</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">512 MB</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Ngôn ngữ hiển thị</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">Tiếng Việt, Tiếng Anh</td>\r\n        </tr>\r\n                                                                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Camera sau</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">8.0 MP</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Camera trước</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last"><a target="_blank" title="Tìm hiểu thêm về: VGA" class="glossary_link vtip" href="http://hc.com.vn/glossary/view/content?id=18">VGA</a> (0.3 Mpx)</td>\r\n        </tr>\r\n                                                                    <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Quay phim</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last"> 	FullHD 1080p@30fps</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Video Call</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">Có</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Băng tần 2G</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">GSM 850/900/1800/1900</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Băng tần 3G, 4G</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">HSDPA 850/900/1900/2100</td>\r\n        </tr>\r\n                                                                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">Loại sim</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">micro sim</td>\r\n        </tr>\r\n                                            <tr class="even">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label">GPS</th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">Có</td>\r\n        </tr>\r\n                                            <tr class="odd">\r\n            <!--Glossary-->\r\n                                    <!--/Glossary-->\r\n            <th class="label"><a target="_blank" title="Tìm hiểu thêm về: Bluetooth" class="glossary_link vtip" href="http://hc.com.vn/glossary/view/content?id=14">Bluetooth</a></th>\r\n            <!--Glossary-->\r\n                                                <!--Glossary-->\r\n            <td class="data last">V4.0 with A2DP</td>\r\n        </tr>\r\n              