--
-- Structure for view `allProductsBrief_view`
--
DROP TABLE IF EXISTS `allProductsBrief_view`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `allProductsBrief_view` AS select `Product`.`productID` AS `productID`,`Product`.`name` AS `name`,`Product`.`image` AS `image`,`Product`.`price` AS `price`,`Product`.`isNew` AS `isNew`,`Product`.`isHot` AS `isHot`,`Product`.`saleOff` AS `saleOff`,`Product`.`dateCreated` AS `dateCreated`,`Category`.`categoryName` AS `categoryName` from (`Product` join `Category`) where (`Product`.`categoryID` = `Category`.`categoryID`);

-- --------------------------------------------------------

--
-- Structure for view `productCart`
--
DROP TABLE IF EXISTS `productCart`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `productCart` AS select `Product`.`productID` AS `id`,`Product`.`quantity` AS `qty`,`Product`.`price` AS `price`,`Product`.`name` AS `name` from `Product`;

-- --------------------------------------------------------

--
-- Structure for view `productDetail_view`
--
DROP TABLE IF EXISTS `productDetail_view`;

CREATE ALGORITHM=TEMPTABLE DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `productDetail_view` AS select `Product`.`productID` AS `productID`,`Product`.`name` AS `name`,`Product`.`image` AS `image`,`Product`.`price` AS `price`,`Product`.`shortInfo` AS `shortInfo`,`Product`.`description` AS `description`,`Product`.`configurationInfo` AS `configurationInfo`,`Product`.`isNew` AS `isNew`,`Product`.`isHot` AS `isHot`,`Product`.`saleOff` AS `saleOff`,`Product`.`quantity` AS `quantity`,`Product`.`status` AS `status`,`Product`.`dateCreated` AS `dateCreated`,`Category`.`categoryName` AS `categoryName`,`Category`.`categoryID` AS `categoryID`,`Supplier`.`name` AS `supplierName` from ((`Product` join `Category`) join `Supplier`) where ((`Product`.`categoryID` = `Category`.`categoryID`) and (`Product`.`supplierID` = `Supplier`.`supplierID`));

