--
-- Structure for view `allproductsbrief_view`
--
DROP TABLE IF EXISTS `allproductsbrief_view`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `allproductsbrief_view` AS select `product`.`productID` AS `productID`,`product`.`name` AS `name`,`product`.`image` AS `image`,`product`.`price` AS `price`,`product`.`isNew` AS `isNew`,`product`.`isHot` AS `isHot`,`product`.`saleOff` AS `saleOff`,`product`.`status` AS `status`,`product`.`quantity` AS `quantity`,`category`.`categoryName` AS `categoryName`,`category`.`categoryID` AS `category_id` from (`product` join `category`) where (`product`.`categoryID` = `category`.`categoryID`) order by `product`.`productID`;

-- --------------------------------------------------------

--
-- Structure for view `customer_review_view`
--
DROP TABLE IF EXISTS `customer_review_view`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `customer_review_view` AS select `customer_review`.`reviewID` AS `reviewID`,`customer_review`.`productID` AS `productID`,`customer_review`.`name` AS `name`,`customer_review`.`vote` AS `vote`,`customer_review`.`comment` AS `comment`,`customer_review`.`time` AS `time` from `customer_review`;

-- --------------------------------------------------------

--
-- Structure for view `feedback_view`
--
DROP TABLE IF EXISTS `feedback_view`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `feedback_view` AS select `feedback`.`feedbackID` AS `feedbackID`,`feedback`.`name` AS `name`,`feedback`.`email` AS `email`,`feedback`.`content` AS `content`,`feedback`.`time` AS `time`,`feedback`.`subject` AS `subject`,`feedback`.`status` AS `status` from `feedback`;

-- --------------------------------------------------------

--
-- Structure for view `new_product_view`
--
DROP TABLE IF EXISTS `new_product_view`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `new_product_view` AS select `product`.`productID` AS `productID`,`product`.`name` AS `name`,`product`.`image` AS `image`,`product`.`price` AS `price`,`product`.`isNew` AS `isNew`,`product`.`isHot` AS `isHot`,`product`.`saleOff` AS `saleOff`,`product`.`status` AS `status`,`product`.`quantity` AS `quantity`,`category`.`categoryName` AS `categoryName`,`product`.`dateCreated` AS `dateCreated` from (`product` join `category`) where (`product`.`categoryID` = `category`.`categoryID`) order by `product`.`dateCreated` desc limit 6;

-- --------------------------------------------------------

--
-- Structure for view `order_detail_view`
--
DROP TABLE IF EXISTS `order_detail_view`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `order_detail_view` AS select `order_details`.`orderItemID` AS `orderItemID`,`order_details`.`productID` AS `productID`,`product`.`name` AS `name`,`order_details`.`quantity` AS `quantity`,`order_details`.`unitPrice` AS `unitPrice`,`order_details`.`orderID` AS `orderID` from (`order_details` join `product`) where (`order_details`.`productID` = `product`.`productID`);

